#include <string>
#include <iostream>

using namespace std;

void hamming_dist(string seq1, string seq2){
    // assuming that both sequences are equal length 
    int dist = 0;
    for(int i = 0; i < seq1.length();i++){
        if(seq1[i] != seq2[i]){
            dist++;
        }
    }

    cout << dist << endl;

}

int main(){
    hamming_dist("","");
    return 0;
}